     public void meterDatos(){
            NombreInp.setText(AdminCursos.nombre);
            nCreditos.setText(AdminCursos.creditos);
            CodigoInp.setText(AdminCursos.codigo);
            nomProfe.setText(AdminCursos.nomProfesor);
            nAlumnos.setText(AdminCursos.alumnos);
        }
