    private void ActualBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ActualBtnActionPerformed
        // TODO add your handling code here:
        String codigo = CodigoInp.getText();
        String nombre = NombreInp.getText();
        String creditos = nCreditos.getText();
        String alumnos = nAlumnos.getText();
        String nomProfesor = nomProfe.getText();


        Curso nuevoCurso = new Curso(codigo, nombre, creditos, alumnos, nomProfesor);
        int fila = AdminCursos.fila;

        AdminCursos.cursos[fila] = nuevoCurso;
    }//GEN-LAST:event_ActualBtnActionPerformed

    private void RegresarBtn(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_RegresarBtn
        AdminCursos cambiaPestaña = new AdminCursos();
        cambiaPestaña.setVisible(true);
        this.dispose();
    }//GEN-LAST:event_RegresarBtn

    
